"""
Tests for the PrimerDesigner class.
"""

import pytest
from primer_designer.config import STANDARD_CONFIG, GC_RICH_CONFIG
from primer_designer.models import SequenceInfo
from primer_designer.primer import PrimerDesigner


class TestPrimerDesigner:
    """Test cases for PrimerDesigner class."""
    
    def setup_method(self):
        """Set up test fixtures."""
        self.designer = PrimerDesigner(STANDARD_CONFIG)
        
        # Test sequence (part of E. coli lacZ gene)
        self.test_sequence = (
            ""
        )
        
        self.sequence_info = SequenceInfo(
            sequence_id="test_seq_1",
            sequence=self.test_sequence,
            description="Test sequence for primer design",
            length=len(self.test_sequence)
        )
    
    def test_primer_designer_initialization(self):
        """Test PrimerDesigner initialization."""
        designer = PrimerDesigner()
        assert designer.config is not None
        
        designer_with_config = PrimerDesigner(GC_RICH_CONFIG)
        assert designer_with_config.config == GC_RICH_CONFIG
    
    def test_design_primer_success(self):
        """Test successful primer design."""
        primer = self.designer.design_primer(self.sequence_info)
        
        assert primer is not None
        assert primer.sequence_id == "test_seq_1"
        assert len(primer.forward_primer) >= 15
        assert len(primer.reverse_primer) >= 15
        assert primer.tm_forward > 0
        assert primer.tm_reverse > 0
        assert 0 <= primer.gc_forward <= 100
        assert 0 <= primer.gc_reverse <= 100
    
    def test_design_primer_short_sequence(self):
        """Test primer design with sequence too short."""
        # This should fail at the Pydantic validation level
        with pytest.raises(ValueError):
            short_sequence = SequenceInfo(
                sequence_id="short_seq",
                sequence="ATGCATGCATGCATGC",  # Only 16 bp
                description="Short test sequence",
                length=16
            )
    
    def test_config_update(self):
        """Test configuration update."""
        original_config = self.designer.config
        self.designer.update_config(GC_RICH_CONFIG)
        
        assert self.designer.config != original_config
        assert self.designer.config == GC_RICH_CONFIG
    
    def test_get_config_summary(self):
        """Test configuration summary."""
        summary = self.designer.get_config_summary()
        
        assert isinstance(summary, dict)
        assert 'primer_size_range' in summary
        assert 'tm_range' in summary
        assert 'gc_range' in summary
        assert 'product_size_range' in summary
